            product: {
                type: mongoose.Schema.Types.ObjectId,
                ref: 'Product',
                /* required: true, */
            },
        },
    ],
    user: {
        type: mongoose.Schema.Types.ObjectId,
        ref: 'User',
        required: true,
    },
    shipping: {
        address: String,
        city: String,
        postalCode: String,
        country: String,
    },
    payment: {
        paymentMethod: String,
        paymentResult: {
            orderID: String,
            payerID: String,
            paymentID: String,
        },
    },
    itemsPrice: Number,
    taxPrice: Number,
    shippingPrice: Number,
    totalPrice: Number,
    isPaid: { type: Boolean, required: true, default: false },
    paidAt: Date,
    isDelivered: { type: Boolean, required: true, default: false },
    deliveredAt: Date,

}, {
    timestamps: true,
});

const Order = mongoose.model('Order', orderSchema);

export default Order;

